  let mut data = String::new();
  f.read_to_string(&mut data).unwrap();
  let mut prog = intcode::parse(&data);
  prog[0] = 2;
  let (in_tx, in_rx) = mpsc::channel();
  let (out_tx, out_rx) = mpsc::channel();
  thread::spawn(move || {
    loop {
      match out_rx.recv() {
        Ok(x) => {
          let y = out_rx.recv().unwrap();
          let val = out_rx.recv().unwrap();
          // eprint!("{} {} {}\n", x, y, val);
          if x == -1 && y == 0 {
            print!("\x1B[27;2H{}\x1B[28;1H", val);
          } else {
            print!("\x1B[{};{}H{}\x1B[28;1H", y+1, x+1,
              match val {
                0 => " ",
                1 => "+",
                2 => "X",
                3 => "=",
                4 => "o",
                _ => panic!(),
              });
          }
          stdout().flush().unwrap();
        },
        Err(_) => {
          break;
        }
      }
    }
  });
